	//Insert tbl_danhmuctruyen
	$sql="INSERT INTO tbl_danhmuctruyen (ID_truyen, tentruyen, loai, tacgia,nguoidang, luotxem, trangthai, mota, img, ngaydang) VALUES
	(1, N'BẤT HỦ PHÀM NHÂN', 5, N'Ta Là Lão Ngũ', N'admin' , 149, N'Còn tiếp', N'Người phàm chỉ có phàm căn, phàm nhân thì không có vĩnh sinh.\r\n\r\nChúng ta đều là phàm nhân, nhưng chúng ta cũng có ngạo cốt và phương thức sinh tồn của riêng mình!\r\n\r\nHãy cùng xem câu chuyện về một người phàm tục Mạc Vô Kỵ, làm cách nào giữa đông đảo cường giả thoát khỏi trùng vây, Chứng Đạo Bất Hủ!\r\n\r\nĐây là truyện mới ra lò, nóng phỏng tay của Lão Ngũ – Bất Hủ Phàm Nhân. Các đạo hữu yêu thích Diệp Mặc và Ninh Thành cùng phong cách viết của Lão Ngũ, hãy cùng đón đọc bộ truyện này nhé.', N'img/kiemhiep1.jpg', N'2016-12-01'),
	(2, N'PHÀM NHÂN TU TIÊN', 5, N'Vong Ngữ', N'admin',  3, N'Hoàn Thành', N'Phàm Nhân Tu Tiên là một câu chuyện Tiên Hiệp kể về Hàn Lập - Một người bình thường nhưng lại gặp vô vàn cơ duyên để bước đi trên con đường tu tiên, không phải anh hùng - cũng chẳng phải tiểu nhân, Hàn Lập từng bước khẳng định mình... Liệu Hàn Lập và người yêu có thể cùng bước trên con đường tu tiên và có một cái kết hoàn mỹ? Những thử thách nào đang chờ đợi bọn họ?\r\n\r\nTruyện kết cấu khá hợp lý, tình tiết không quá chậm, không quá nhanh, diễn tả khá đặc biệt, lời văn trôi chảy, nhân vật tính cách đặc thù. Nhân vật chính, Hàn Lập, mang hơi hướng khá cô độc. Bạn nào thích đọc Tru Tiên hoặc Thương Thiên chắc sẽ thích Phàm Nhân Tu Tiên Truyện. Mời bạn đọc cùng thưởng thức và dõi theo bước chân của Hàn Lập!', N'img/tienhiep1.jpg', N'2016-12-01'),
	(3, N'ĐẠI VIỆT TU CHÂN', 1, N'Trần Đức Tiến', N'admin',  0, N'Còn tiếp', N'Một vũ trụ nào đó tồn tại hàng tỷ năm còn trong hỗn độn. Nhờ khí hỗn độn tạo thành 2 cường giả đại năng. Sau đó 2 cường giả chiến đấu 1 trận long trời lở đất phá tan hỗn độn và dần hình thành thế giới. Thế giới phân tách ra làm 2 phần: tiên giới và đại lục.\r\nTiên giới chia ra làm 4 giơi: Phong giới, Lôi giới và Vũ giới, Hỏa Vực giới (bị thất lạc)\r\nĐại lục: 4 thượng vưc và 13 hạ vưc. (update tên sau)\r\n\r\nTrải qua hơn 1000 vạn năm, mọi việc dần trở thành tự nhiên. Cho tới 10 vạn năm trước, đại lục xuất hiện 2 đại kỳ tài là Lạc Long Quân và Âu Cơ, 2 người kết thành đạo lữ và phá toái hư không phi thăng tiên giới thì tiên giới xuất hiện dị trạng khiến cửa vào tiên giới bị phong bế.\r\n\r\nCấp độ trong truyện:\r\nTu Chân: luyện khí, trúc cơ, kim đan, nguyên anh, hư thần, kiếp biến, hoá thần, vấn đỉnh và đại thừa. \r\nTiên giới: hư tiên, chân tiên, kim tiên, huyền tiên, đại la tiên, tiên vương, tiên đế, đại thánh, thánh vương\r\nHỗn nguyên: thánh nhân, chân quân, thiên quân. \r\nHỗn độn đại năng\r\n\r\nYêu thú: \r\nCấp 1-9 tương đương với 9 cấp tu chân (cấp 7 trở lên có thể may mắn hoá hình)\r\nMa thú: Cấp 1-9 tương ứng với 9 cấp tiên giới (cấp 7 trở lên nới có thể hoá hình)\r\nMỗi cấp chia làm: sơ giai, trung giai, hậu giai và viên mãn.\r\n\r\nPháp khí: \r\nĐại lục: hoàng, huyền, địa, thiên \r\nTiên giới: tiên, thánh, thần, hỗn độn\r\nVõ công: \r\nĐại lục: hoàng, huyền, địa, thiên \r\nTiên giới: tiên, thánh,thần, hỗn nguyên \r\nMỗi cấp chia làm: sơ, trung, thượng phẩm \r\n\r\nLuyện đan và phù, trận pháp \r\nCấp linh đan, phù,: 1-9 tương ứng 9 cấp tu chân \r\nCấp tiên đan, phù: 1-9 tương ứng 9 cấp tiên giới\r\n\r\nP/s 1: Các tên danh nhân VN có xuất hiện nhưng không liên quan gì tới lịch sử, hoặc thần thoại VN nhé.\r\n\r\nP/s 2: Vì đây là truyện VN, nên từ ngữ sẽ phù hợp với từ ngữ thuần Việt hơn là dùng từ Hán Việt. Vì thế giới tiên hiệp VN, nên mọi người cứ góp ý thoải mái\r\n\r\n', N'img/kiemhiep2.jpg', N'2016-12-01'),
	(4, N'THIÊN TỐNG', 1, N'Hà Tả', N'admin',  0, N'Còn tiếp', N'Một cảnh sát cao cấp nổi tiếng cấp quốc tế với tiêu chuẩn đạo đức của mình, xuyên việt đến những năm cuối của Bắc Tống với gian thần đầy rẫy và một tên hoàng đế ngu ngốc.\r\n\r\nBộ truyện không phải là truyện tranh bá, cũng không phải truyện thống trị thiết huyết.\r\n\r\nCường thế không thể làm chủ tất cả, thông minh là căn bản của hết thảy. Có lẽ 40% thông minh, 40% cố gắng, cộng thêm 20% may mắn chính là tất cả mà bộ truyện muốn nói.\r\n\r\nPS: Truyện lịch sử có BUG, truyện lịch sử không BUG không phải truyện lịch sử hay.\r\n\r\nBộ truyện này được viết bởi Hà Tả, đậm chất hài hước xem kẽ yếu tố tranh đấu, âm mưu tính kế. Hi vọng mọi người đọc truyện vui vẻ!', N'img/kiemhiep3.jpg', N'2016-11-02'),
	(5, N'BÍ MẬT MẢNH GHÉP QUỶ', 4, N'Kevin Rz', N'admin',  1, N'Hoàn thành', N'Vy Băng - Chuyển vào trường danh dá nhưng luôn bị coi thường và đánh đập nhưng cô nào nể. Rồi bất ngờ người cha đã xa cách năm cô 3 tuổi quay lại và cho cô biết...cô chính là con của ông.. Ông lại là trùm của bang xã hội đen có tăm tiếng... Một cô nàng ương bướng có chút tự cao nhưng vì quá khứ và tình yêu của quá khứ. Nỗi thù hận mất mát do một người tạo ra đã khiến cô trở nên dữ tính lạnh lùng và...lại là sát thủ trong bóng đêm.\r\n\r\nThiên Phong - cậu sinh ra được ông trời ban cho một vẻ đẹp hoàn mĩ đến từng chi tiết. Khuôn mặt ấy khiến cô gái nào cũng phải say mê vẻ đẹp ấy. Cậu lạnh lùng và sống buông thả. Cậu tàn ác và sống với trái tim của một con quỷ. Cậu là trùm của tổ chức xã hội đen lớn mang tên Devil bloodthirsty. Cậu có quá khứ đau đớn với mối tình đầu tiên.\r\n\r\nBăng và Phong đều đau khổ với mối tình đầu. Hai người gặp nhau chỉ biết đá đểu và sừng cổ chêu ghẹo nhau. !!\r\nNhững ngày tháng bên nhau trải qua nhiều gian nan và một màu đen tối của kẻ thù luôn kè sát họ. Liệu họ có quên được quá khứ, chiến thắng kẻ thù..cùng nhau sánh bước trên con đường mang tên hạnh phúc vĩnh cửu? Câu chuyện với những tình huống hài hước. Những khoảnh khắc lãng mạn giữa tình yêu tuổi học trò và khoảnh khắc nguy hiểm giữa giang hồ. Nó giúp ta am hiểu được nhiều thứ ý nghĩ trong tình yêu và tình bạn.\r\n\r\nMuốn giữ chặt hạnh phúc thì phải biết rộng lượng thứ tha và không quá ép buộc. Cũng giống như bàn tay ta nắm một đống cát. Càng siết chặt tay cát càng rơi ra bên ngoài nhưng ta biết lơi lỏng nhẹ nhàng. Cát sẽ vẫn ở trong bàn tay ta...\r\n\r\n__Giới thiệu nhân vật chính nhất___\r\n1. Hạ Lý Vy Băng : Girl : Có gương mặt không gọi là quá xinh đẹp hoàn hảo như hotgirl nhưng rất dễ thương. Chiều cao khiêm tốn nhưng tuyệt đối không đi giày cao gót. Cao 1m61 Biết nhiều loại võ nhưng giỏi nhất là karate\r\n- Tính cách : Sáng nắng chiều mưa trưa gió mùa Đông Bắc. Về sau rất lạnh lùng\r\n- Gia thế : Đọc rồi biết...không biết nói sao...\r\n2. Hà Kỷ Lam : Girl : Bạn thân Vy Băng. Có khuôn mặt xinh xắn. Chiều cao lý tưởng 1m71. Có võ phòng thân\r\n- Tính cách : Vui vẻ nhưng nếu ai động đến là rất..bà..chằn\r\n- Gia thế : Con gái của một nhà giàu có quyền lực làm ăn ở địa bàn\r\n3 : Hứa Thiên Phong : Boy : Sở hữu một gương mặt hoàn mĩ không thể hoàn mĩ hơn. Mắt nâu hổ phách. Mũi cao. Lông mi dài đẹp. Môi đỏ mọng. Có mái tóc màu nâu... Là hotboy mà bất cứ girl nào nhìn cũng siêu lòng vị độ đẹp trai nghiêng nước nghiêng thành. Cao 1m86\r\n- Tính cách : Lạnh lùng vô cảm. Tàn nhẫn độc ác\r\n- Gia thế : Là thiếu gia của dòng họ Hứa danh dá nổi tiếng. Giàu có coi tiền như rác. Gia đình cậu có rất nhiều khách sạn trên toàn cầu. Số siêu xe mà Phong thay đổi nhiều không đem xuể Phong là trùm của nhiều bang xã hội đen đặc biệt là Devil bloodthirsty.\r\nCậu giỏi trong mọi lĩnh vực và thành thạo rất nhiều thứ võ..như karate, kiếm đạo, bắn súng, taekwondo.,..\r\n4 : Phạm Hiểu Vĩ : Boy : Đẹp rạng ngời không cần nhiều lời. Cao 1m84. Giỏi võ Vovinam và Karate\r\n- Tính cách : Chung thuỷ duy nhất với một người. Hoà đồng và hài hước..nhưng một khi ai động đến thì không khác gì ác quỷ giống Phong\r\n- Gia thế : Giàu có khỏi nói. Cha mẹ sống trong tp.HCM . Bố là chủ tịch tập đoàn đá quý lớn và sản xuất oto, tàu điện ngầm và máy bay. Có một em gái tên Hiểu Đồng\r\n5 : Nhật Minh Khánh : Boy : Khuôn mặt đẹp như khắc. Không diễn tả thành lời. Cao 1m84. Rất giỏi võ đặc biệt là Võ thuật cổ truyền và Judo\r\n- Tính cách : Như Hiểu Vĩ\r\n- Gia thế : Dòng họ quý tộc. Cha mẹ sống và làm việc bên Mĩ. Gia đình cậu là chủ nhiều tập đoàn lớn mạnh khắp nước. Có em gái tên Minh Như hiện đang sống trong tp.HCM cùng gia đình Hiểu Vĩ.\r\n6 : Lưu Trịnh Huy ( Ken ) : Nói là thiên thần thì không phải nhưng ác quỷ thì cũng không? Cậu đẹp trai mà bất kì ai cũng muốn đẹp bằng cậu. Đẹp không tì vết không thể chê vào đâu được. Một khi cậu nở nụ cười thì tất cả phải dao động...Cao 1m85. Rất giỏi võ..biết cả vịnh xuân quyền\r\n- Tính cách : Lạnh lùng...quan tâm bằng trái tim chứ không hiện ra bên ngoài là mấy\r\n- Gia thế : Con trai độc nhất của dòng họ Lưu. Bố mẹ cậu sống và làm việc bên Mĩ cùng bố mẹ Khánh. Là những ông trùm của thế giới kinh doanh không ai có thể vượt qua. Cậu cũng có dính dáng trong giang hồ. Là boss của bang Viboss máu mặt.\r\n..........\r\nVà các nhân vật chính phụ khác...', N'img/kybi1.jpg', N'2016-11-01'),
	(6, N'CẤM HUYẾT HỒNG LIÊN', 8, N'Trư Hồng Huyết', N'admin',  0, N'Hoàn thành', N'Vài lời giới thiệu:\r\n\r\nKhung Võ đại lục là thế giới của ma pháp và đấu khí, ngoài ra, còn có một hệ thống võ học rất đặc biệt, mạnh nhất cũng là yếu nhất- Huyết Kế công pháp.\r\n\r\nMột kẻ không thể tu luyện đấu khí gia truyền, là phế vật bị gia tộc ruồng bỏ, bất ngờ lại được tổ khí của gia tộc đã lưu truyền vạn năm, tổ khí tên gọi là Hồng Liên…\r\n\r\nMỗi người đều có một loại lửa từ dưới đáy lòng, một khi châm nó lên, chuyện gì cũng có thể xảy ra! Ma pháp sáng ngời, đấu khí cương mãnh, tôi luyện ma binh, đây chính là Cấm Huyết Hồng Liên!..', N'img/maphap1.jpg', N'2016-10-06'),
	(7, N'ÁI PHI TUYỆT SẮC CỦA THẦN BÍ VƯƠNG GIA', N'admin',  6, N'Đạm Nguỵêt Tân Lương', 0, N'Còn tiếp', N'Nàng là Tịch Nhan quận chúa có dung mạo tuyệt sắc, khuynh quốc khuynh thành, là đệ nhất mỹ nhân trong thiên hạ, chỉ một cái liếc mắt có thể làm thành trì nghiêng ngả, chúng sinh điên đảo...\r\n\r\n\r\n- Theo truyền thuyết dân gian, nhan sắc của nàng thậm chí hấp dẫn ngay cả phụ thân, do vậy mà bức tử cả chính mẫu thân của mình, làm cho phụ thân biến mất không tung tích. Thánh Thượng khâm điểm nàng hòa thân nước láng giềng, bị các hoàng tử âm mưu tranh giành, nàng vẫn thản nhiên. \r\n\r\n- Trước đêm tứ hôn, một trận hỏa hoạn ngoài ý muốn xảy ra, nàng che dấu thành công dung nhan tuyệt sắc của mình, biến thành một cô gái vô cùng xấu xí, thoạt nhìn đây là một trận hỏa hoạn ngoài ý muốn, giúp cho nàng thành công che dấu dung nhan tuyệt sắc của mình, trở nên xấu xí dọa mọi người hoảng sợ. \r\n\r\n- Nhưng mà, vẫn có một người nguyện ý cưới nàng? Người đó rất ít xuất hiện trước mặt mọi người, theo truyền thuyết đó là Thất hoàng tử bị tàn phế. Khắp thiên hạ đều nhạo báng nàng – Xú phi của tàn vương gia. \r\n\r\n- Đêm đại hôn, chàng ngồi xe lăn còn nàng khăn đen che mặt. Nhưng sao trong truyền thuyết lại không cho nàng biết vị Thất hoàng tử này lại tuấn mỹ như vậy? Chuyện gì xảy ra tiếp theo? Mời bạn cùng đọc sẽ rõ! N', N'img/ngontinh1.jpg', N'2016-11-10'),
	(8, N'365 Ngày Hôn Nhân', 6, N'Guai Wu', N'admin',  0, N'Còn tiếp', N'- Lãnh Tử Tình, 25 tuổi, trong mắt người khác là một cô gái trầm mặc ít lời, không chí tiến thủ, cũng là một nhà văn được yêu thích trên mạng. Lôi Tuấn Vũ, 33 tuổi, giám đốc tập đoàn Kiêu Dương. Hai người từ nhỏ đã ấn định hôn ước, vì yêu cầu bức thiết của hai bên cha mẹ liền đồng ý kết hôn.\r\n\r\n- Nhưng không ai biết, ở trước hôn kỳ, hai người nhất trí lập hợp đồng hôn nhân, chỉ có 365 ngày, ước định rất đơn giản, cấp đôi bên đầy đủ tự do… Kết hôn không lâu, hắn đã vi phạm hợp đồng, cưỡng ép cô… Nhưng lớn tiếng thổ lộ ra, lại là tên người phụ nữ khác… Tuyệt vọng xoẹt qua đáy mắt, cô cười lạnh đóng gói hành lý, rời nhà trốn đi! Đều nói “hồng nhan bạc mệnh”, cô chẳng phải hồng nhan, sao cũng giống trong tiểu thuyết bị mất trí nhớ? \r\n\r\n- “Anh là ai?” Sự sợ hãi tràn dâng trong lòng, người trước mặt này, cô chưa bao giờ gặp qua. Đáy mắt người đàn ông xẹt qua một tia sáng, nâng cằm cô lên: “Tôi là ai không quan trọng, quan trọng là, tôi là người đàn ông của em!” Sau khi khôi phục lại trí nhớ, cô trong lòng thầm chửi hắn trăm ngàn lần! Anh là người đàn ông của tôi?! Đúng mới là lạ! Lại đóng gói hành lý một lần nữa, cùng nắm tay người đàn ông khác, bỏ trốn! Nhìn những đoạn miêu tả mấy màn mây mưa thất thường trong tiểu thuyết của cô, con ngươi người đàn ông bắn ra ánh mắt khát máu muốn giết người: Lãnh Tử Tình, em được lắm, đem tôi ra để thể nghiệm cuộc sống?! Trò chơi của em đã kết thúc? Nhưng trò chơi của tôi chỉ vừa mới bắt đầu! N', N'img/ngontinh2.jpg', N'2016-10-21'),
	(9, N'Cạm Bẫy Hôn Nhân', 6, N'Lưu Lãng Hồng Tường Vi', N'admin',  0, N'Hoàn thành', N'- Đêm tân hôn, chờ đợi cô không phải người chồng dịu dàng che chở, mà là một trò chơi đổi vợ tàn khốc.Thân là cô dâu, lại bị chồng mình tự tay đẩy vào vòng tay người đàn ông khác, hung hăng chà đạp tự ái của cô. \r\n\r\n- Cô bị thuốc mê khống chế, bị buộc đón nhận hoan ái ở dưới thân thể của người đàn ông khác, ba ngày ba đêm, không ngủ không nghỉ... \r\n\r\n- Trước mặt mọi người, bọn họ có một cuộc hôn nhân hạnh phúc mỹ mãn, người người yêu thích và ngưỡng mộ.Sau lưng người, cô chẳng qua là nữ đầy tớ làm ấm giường của anh, bị anh bừa bãi lăng nhục, “Cô chẳng qua chỉ là một phụ nữ dâm tiện, không có lệnh của tôi, cho dù cô muốn chết cũng không được!” \r\n\r\n- Thật ra văn án hơi lừa tình một chút, truyện cũng không quá mức tàn khốc như thế, nhưng đúng là loại cường thủ hào đoạt. \r\n\r\n- Theo cảm nhận riêng của tớ (người dịch)truyện không ngược lắm. Nhưng tớ nghĩ là sau đó thì anh nam chính đã trả giá đủ và con đường truy vợ cũng thật cam go. Gần cuối truyện sẽ không thiếu những cảnh đáng yêu của 2 em bé, 1 bé rất dễ thương, 1 bé rất thông minh. Cái tớ thích nhất của truyện chắc là... có 3 anh nam đều vô cùng đủ tiêu chuẩn :) N', N'img/ngontinh3.jpg', N'2016-10-11'),
	(10, N'CÔNG TỬ ĐIÊN KHÙNG', 5, N'Ta Là Lão Ngũ', N'admin',  0, N'Còn tiếp', N'Giới thiệu của Tác giả:\r\n\r\nCông Tử Điên Khùng - Từ Địa Cầu xuyên việt tới Dị giới đã thấy nhiều, nhưng Lâm Vân của chúng ta lại từ Dị giới xuyên tới Địa Cầu. Điều này còn chưa phải là buồn bực nhất, để cho Lâm Vân phát điên chính là, hắn đang nằm trong một bệnh viện tâm thần...\r\n\r\nGiới thiệu của Dịch giả:\r\n\r\n- Đây có thể nói là bộ đô thị mà mình thấy hay nhất. Trong đó có đủ tất cà các thứ mà một độc giả cần, đó là tình cảm, nhiệt huyết, phiêu lưu mạo hiểm. Trong truyện bạn sẽ gặp một nhân vật Lâm Vân tràn đầy tình cảm với người yêu, nhưng lại lãnh khốc với kẻ thù, một nhân vật kỳ tài nhưng không thích phô diễn. Xuyên suốt bộ truyện là hành trình đi tìm lực lượng đỉnh cao của nhân vật chính. Những cuộc chiến dữ dội để bảo vệ người thân của mình.\r\n\r\n- Điều đặc biệt hơn trong truyện mà mình ít thấy ở những truyện khác, đó là tác giả miêu tả tình cảm nam nữ rất sâu sắc, lắng đọc. Nhân vật nữ không bình hoa như các truyện khác, nhân vật nam cũng không phong lưu, thấy cô gái xinh đẹp nào là thu về, mà tình cảm diễn ra rất tự nhiên. Nói tóm lại, đây là bộ đô thị mà những người yêu truyện trên mạng không thể bỏ qua.', N'img/ngu-thien-than-de.jpg', N'2016-11-16'),
	(11, N'ÁC MA PHÁP TẮC', 2, N'Khiêu Vũ', N'admin' , 0, N'Còn tiếp', N'- Thế giới trong truyện pha trộn giữa khoa học hiện đại và ma pháp. Nhân vật chính – Đỗ Duy – là con trưởng trong gia đình quyền quý, sau khi đầu thai vẫn giữ nguyên kiến thức từ kiếp trước. Do cơ thể và kiến thức không thích nghi được nên từ khi sinh ra, hắn không chịu nói, cơ thể gầy yếu khác hẳn những thành viên khác trong dòng họ. Sau bao nỗ lực của gia đình, hắn vẫn không học được văn, võ hay ma pháp, vì vậy hắn bị gia đình ruồng bỏ, trao hết quyền lợi cho em trai hắn...\r\n- Truyện đang đứng thứ 3 trong nhóm truyện Cường lực thôi tiến tại trang Bạch Mã Thư Viện. Với sức tưởng tượng vô cùng phong phú thêm vào ngòi bút chắc chắn của tác giả Khiêu Vũ, các bạn sẽ được đến với một thế giới cổ xưa toàn cảnh, một thế giới có những kỵ sĩ, có quái vật, có ma pháp sư, có quý tộc, có thị dân, có nô lệ, và có cả những điều kỳ diệu...\r\n\r\n- Không giống như các truyện khác hiện tại, Ác Ma Pháp Tắc tiến triển sẽ rất chậm vì tác giả tả kỹ tất cả mọi thứ, từ khung cảnh, lịch sử, văn hóa, thậm chí là từng hình dạng đồ vật cho tới ý nghĩ trong lòng nhân vật... Dưới ngòi bút của tác giả, độc giả có thể nhìn thấy toàn cảnh một thế giới cổ xưa, những đấu tranh chính trị, đấu tranh tôn giáo, chiến trận... Điều này sẽ có thể khiến một vài chương truyện có vẻ hơi nhàm chán vì tác giả tả những thứ linh tinh mà mọi người không muốn xem nhưng khi ghép chúng lại các bạn có thể nhìn thấy được một toàn cảnh tráng lệ, chi tiết. Ác Ma Pháp Tắc hoàn toàn có thể sánh ngang với: Tru Tiên, Thương Thiên và Liên Hoa Bảo Giám...', N'img/tai-tu-tien-gioi-choi.jpg', N'2016-11-08');";
